  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'troubleshooting',
	title: 'Troubleshooting'
};
export const metadata = {
  "unversionedId": "troubleshooting",
  "id": "troubleshooting",
  "isDocsHomePage": false,
  "title": "Troubleshooting",
  "description": "Accessing Zeitgeist through polkadot.js.org/apps",
  "source": "@site/docs/troubleshooting.md",
  "slug": "/troubleshooting",
  "permalink": "/troubleshooting",
  "editUrl": "https://github.com/zeitgeistpm/docs/edit/main/docs/troubleshooting.md",
  "version": "current",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Using the Faucet",
    "permalink": "/using-the-faucet"
  },
  "next": {
    "title": "Guides",
    "permalink": "/guides"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const rightToc = [{
  value: 'Accessing Zeitgeist through polkadot.js.org/apps',
  id: 'accessing-zeitgeist-through-polkadotjsorgapps',
  children: []
}, {
  value: 'Accessing Zeitgeist through the SDK',
  id: 'accessing-zeitgeist-through-the-sdk',
  children: []
}, {
  value: 'Accessing Zeitgeist through the CLI',
  id: 'accessing-zeitgeist-through-the-cli',
  children: []
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h3 {...{
      "id": "accessing-zeitgeist-through-polkadotjsorgapps"
    }}>{`Accessing Zeitgeist through `}<a parentName="h3" {...{
        "href": "https://polkadot.js.org/apps/?rpc=wss%3A%2F%2Fbp-rpc.zeitgeist.pm"
      }}>{`polkadot.js.org/apps`}</a></h3>
    <h5 {...{
      "id": "i-see-unknown-as-the-result-of-my-query-to-chain-state-or-from-making-an-rpc-call"
    }}>{`I see `}<inlineCode parentName="h5">{`<unknown>`}</inlineCode>{` as the result of my query to Chain state, or from making an RPC call`}</h5>
    <p>{`Most of the time, this is the result of misconfugred types in
polkadot.js.org/apps. However, this result will also show for a brief moment
while the page is waiting for the network request, so if the network is very
slow or the connection breaks, then `}<inlineCode parentName="p">{`<unkown>`}</inlineCode>{` will continue to show`}</p>
    <p>{`To diagnose which of these problems you are experiencing, open your browser's
Dev Tools. Usually you can do this by pressing F12. Dev Tools contains a console
(you may have to select the tab for it) which outputs errors encountered by the
page. If you see an error along the lines of:`}</p>
    <blockquote>
      <p parentName="blockquote">{`DRR: Unable to decode ... createType ... Decoded input doesn't match input,
received 0x..... , created 0x.....`}</p>
    </blockquote>
    <p>{`it is because polkadot.js.org/apps in your browser is using a different set of
type definitions than the Zeitgeist chain itself.`}</p>
    <p>{`polkadot.js.org/apps fetches type definitions from
`}<a parentName="p" {...{
        "href": "https://www.npmjs.com/search?q=%40zeitgeistpm"
      }}>{`our bundled packages`}</a>{` on npm.
Sometimes we have to upgrade the type definitions to support new features and
polkadot.js/apps can take up to 24 hours to fetch the new definitions`}</p>
    <p>{`Npm is almost always up to date with the types we are using on the Zeitgeist
chain but you can check by comparing the version number of
`}<a parentName="p" {...{
        "href": "https://www.npmjs.com/package/@zeitgeistpm/type-defs"
      }}>{`our published npm package`}</a>{`
with that on
`}<a parentName="p" {...{
        "href": "https://github.com/zeitgeistpm/tools/blob/main/packages/type-defs/package.json"
      }}>{`our Github repository`}</a>{`.`}</p>
    <p>{`If you find the type defintions mismatching, you can easily update them manually
by taking
`}<a parentName="p" {...{
        "href": "https://github.com/zeitgeistpm/tools/blob/main/packages/type-defs/src/index.ts"
      }}>{`the new type definitions from the Github repository`}</a>{`,
selecting only the object named `}<inlineCode parentName="p">{`types`}</inlineCode>{`, and copying and pasting it into
`}<a parentName="p" {...{
        "href": "https://polkadot.js.org/apps/?rpc=wss%3A%2F%2Fbp-rpc.zeitgeist.pm#/settings/developer"
      }}>{`the Settings | Developer tab`}</a>{`
in polkadot.js/apps. Don't forget to press `}<inlineCode parentName="p">{`Save`}</inlineCode>{`!`}</p>
    <p><img alt={"selecting type definitions"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../static/img/type-def_selection.png").default} /></p>
    <p>{`If you cannot `}<inlineCode parentName="p">{`Save`}</inlineCode>{` the new types and the input area remains pink, ensure that the object is strictly JSON formatted, for example by using Javascript's `}<inlineCode parentName="p">{`JSON.stringify`}</inlineCode>{` method or `}<a parentName="p" {...{
        "href": "https://www.freeformatter.com/json-formatter.html"
      }}>{`a formatting tool`}</a>{`.`}</p>
    <h3 {...{
      "id": "accessing-zeitgeist-through-the-sdk"
    }}>{`Accessing Zeitgeist through the `}<a parentName="h3" {...{
        "href": "https://github.com/zeitgeistpm/tools"
      }}>{`SDK`}</a></h3>
    <h5 {...{
      "id": "i-want-to-use-the-sdk-but-its-doing-something-i-didnt-expect"
    }}>{`I want to use the SDK, but it's doing something I didn't expect!`}</h5>
    <p>{`We will support the SDK after is it released. Don't worry, you won't have too
long to wait!`}</p>
    <h3 {...{
      "id": "accessing-zeitgeist-through-the-cli"
    }}>{`Accessing Zeitgeist through the `}<a parentName="h3" {...{
        "href": "https://github.com/zeitgeistpm/tools"
      }}>{`CLI`}</a></h3>
    <h5 {...{
      "id": "i-want-to-use-the-cli-but-its-doing-something-i-didnt-expect"
    }}>{`I want to use the CLI, but it's doing something I didn't expect!`}</h5>
    <p>{`Sorry - you need to wait for the SDK to be released!`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
