  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'using-the-faucet',
	title: 'Using the Faucet'
};
export const metadata = {
  "unversionedId": "using-the-faucet",
  "id": "using-the-faucet",
  "isDocsHomePage": false,
  "title": "Using the Faucet",
  "description": "Zeitgeist's test network Battery Park uses the ZBP token,",
  "source": "@site/docs/using-the-faucet.md",
  "slug": "/using-the-faucet",
  "permalink": "/using-the-faucet",
  "editUrl": "https://github.com/zeitgeistpm/docs/edit/main/docs/using-the-faucet.md",
  "version": "current",
  "sidebar": "someSidebar",
  "previous": {
    "title": "How To Participate in Zeitgeist's Kusama Parachain Derby",
    "permalink": "/how-to-participate-in-derby"
  },
  "next": {
    "title": "Troubleshooting",
    "permalink": "/troubleshooting"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const rightToc = [{
  value: 'Getting ZBP',
  id: 'getting-zbp',
  children: [{
    value: 'Using the !drip command',
    id: 'using-the-drip-command',
    children: []
  }, {
    value: 'Using the !claim command',
    id: 'using-the-claim-command',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Zeitgeist's test network `}<a parentName="p" {...{
        "href": "./battery-park"
      }}>{`Battery Park`}</a>{` uses the ZBP token,
instead of the ZTG. ZBP is provided to users through a frictioned faucet on
Discord in order to allow for testing and experimentation.`}</p>
    <p><strong parentName="p">{`NOTE:`}</strong>{` Due to the presence of a click-farm attack on our faucet we needed to take an
extra measure to protect the integrity of the Kusama Derby campaign. For this reason,
we decided to put the faucet behind a $2.00 paywall. All of the proceeds will be donated to India
COVID Relief Funds. We hope you understand. The faucet can now be accessed from `}<a parentName="p" {...{
        "href": "https://launchpass.com/kusama-derby-faucet/faucet"
      }}>{`this link`}</a>{`.`}</p>
    <h2 {...{
      "id": "getting-zbp"
    }}>{`Getting ZBP`}</h2>
    <h3 {...{
      "id": "using-the-drip-command"
    }}>{`Using the !drip command`}</h3>
    <p>{`The normal way to get ZBP from the faucet is to use the !drip command.`}</p>
    <ul>
      <li parentName="ul">{`First create a new Zeitgeist account (standard Substrate account) using the
Polkadot-JS Extension that is available
`}<a parentName="li" {...{
          "href": "https://polkadot.js.org/extension/"
        }}>{`here`}</a>{`.`}</li>
      <li parentName="ul">{`Follow the instructions
`}<a parentName="li" {...{
          "href": "./how-to-participate-in-derby#create-a-zeitgeist-account"
        }}>{`here`}</a>{` for creating
an account.`}</li>
      <li parentName="ul">{`Go to Zeitgeist's Discord server and enter into the #faucet channel.`}</li>
      <li parentName="ul">{`Copy your address from the extension, ensure that it's in Substrate generic
format (begins with a "5").`}</li>
      <li parentName="ul">{`Type in `}<inlineCode parentName="li">{`!drip <address>`}</inlineCode>{` to the faucet channel.`}</li>
      <li parentName="ul">{`The faucet should respond that it sent you some ZBP.`}</li>
      <li parentName="ul">{`After completing this you will have a 24 hour cooldown until you can get more
ZBP.`}</li>
    </ul>
    <h3 {...{
      "id": "using-the-claim-command"
    }}>{`Using the !claim command`}</h3>
    <p>{`As a special bonus to KSM holders during the
`}<a parentName="p" {...{
        "href": "https://proto.zeitgeist.pm/kusama-derby"
      }}>{`Kusama Derby`}</a>{`, the faucet will allow
any account that held KSM on the snapshot day of May 11, 2021 to claim an
equivalent amount of ZBP for usage on the testnet.`}</p>
    <p>{`In order to make the claim you will need to sign a message using your KSM
account. There may be multiple ways to do this but for this guide we show you
using Polkadot-JS Apps, which is available `}<a parentName="p" {...{
        "href": "https://polkadot.js.org/apps"
      }}>{`here`}</a>{`.`}</p>
    <p>{`Go to Apps and switch the network to Kusama.`}</p>
    <p><img alt={"apps kusama"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../static/img/apps_kusama.png").default} /></p>
    <p>{`Go to the "Developer" and select "Sign and Verify".`}</p>
    <p><img alt={"sign and verify"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../static/img/sign_and_verify.png").default} /></p>
    <p>{`Now it is expected that you have a Zeitgeist account (Substrate standard
account) ready, if you do not please follow the instructions
`}<a parentName="p" {...{
        "href": "./how-to-participate-in-derby#create-a-zeitgeist-account"
      }}>{`here`}</a>{` to create one.`}</p>
    <p>{`Copy and paste your Zeitgeist account address into the field that says "sign the
following data". Click "Sign message" and confirm any prompts that ask for
confirmation.`}</p>
    <p>{`Now you should have a hex string that looks like this:`}</p>
    <p><inlineCode parentName="p">{`0xbee763936b767363bce2f102b87e4da48a74732d94385e29e72c0cb77e39f1680b7edd006721887807169d0122c77a471e8cae1cb710cfd7839a41e07909ff82`}</inlineCode></p>
    <p>{`Now you will go to the faucet and type the following command, using your own KSM
address, ZTG address, and signed message as the arguments:`}</p>
    <pre><code parentName="pre" {...{}}>{`!claim <ztg_address> <ksm_address> <signed_message>
`}</code></pre>
    <p>{`If all is successful you will see the faucet respond that it sent you an
equivalent amount of ZBP as your Kusama account had on the snapshot day. This
claim can only be done once.`}</p>
    <p><img alt={"faucet claim"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!./../static/img/faucet_claim.png").default} /></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
